                    nextEl: '.swiper-button-next',
                    prevEl: '.swiper-button-prev',
                },
                breakpoints: {
                    320: {
                        slidesPerView: 1.1,
                        spaceBetween: 16,
                        centeredSlides: true,
                    },
                    768: {
                        slidesPerView: 2.2,
                        spaceBetween: 20,
                        centeredSlides: false,
