                    1024: {
                        slidesPerView: 3.2,
                        spaceBetween: 20,
                    }
                }
            });

            // Initialize Swiper for '.testimonials-swiper'
            initializeSwiper('.testimonials-swiper', {
                slidesPerView: 'auto',
                centeredSlides: true,
                breakpoints: {
                    640: {
